অনুষ্ঠান যোগ(ক, খ) {
  প্রেরণ ক + খ
}

সংখ্যা ফলাফল = যোগ(৫, ৩)
লিখো(ফলাফল) // ৮

সংখ্যা সংখ্যা = ১০

যদি (সংখ্যা > ০) {
  লিখো("ধনাত্মক")
} নাহলে {
  লিখো("ঋণাত্মক বা শূন্য")
}

// যখন লুপ
সংখ্যা গণনা = ০
যখন (গণনা < ৫) {
  লিখো(গণনা)
  গণনা = গণনা + ১
}

// জন্য লুপ
জন্য (সংখ্যা আই = ০; আই < ৫; আই = আই + ১) {
  লিখো(আই)
}

const banglascript = require('banglascript');

const banglaCode = `
সংখ্যা নাম = "বিশ্ব"
লিখো("হ্যালো, " + নাম + "!")
`;

const { code } = banglascript.transpileWithSourceMap(banglaCode);
console.log(code);

require('banglascript/register');

// এখন সরাসরি .bjs ফাইল require করা যাবে
const myModule = require('./myModule.bjs');

চেষ্টা {
  // এমন কোড যা ত্রুটি দিতে পারে
  সংখ্যা ফলাফল = বিপজ্জনক_অনুষ্ঠান()
  লিখো(ফলাফল)
} ধরো (ত্রুটি) {
  সমস্যা_লিখো("একটি ত্রুটি ঘটেছে: " + ত্রুটি)
} অবশেষে {
  লিখো("পরিচালনা সম্পন্ন")
}
