sections.code:
ফাংশন যোগ(ক, খ) {
  ফেরত ক + খ
}

ধরি ফলাফল = যোগ(৫, ৩)
লেখো(ফলাফল) // ৮

ধরি সংখ্যা = ১০

যদি (সংখ্যা > ০) {
  লেখো("ধনাত্মক")
} নাহলে যদি (সংখ্যা < ০) {
  লেখো("ঋণাত্মক")
} নাহলে {
  লেখো("শূন্য")
}

// যতক্ষণ লুপ
ধরি গণনা = ০
যতক্ষণ (গণনা < ৫) {
  লেখো(গণনা)
  গণনা = গণনা + ১
}

// প্রতিটির জন্য লুপ
প্রতিটির জন্য (ধরি আই = ০; আই < ৫; আই = আই + ১) {
  লেখো(আই)
}

const banglascript = require('banglascript');

const banglaCode = `
ধরি নাম = "বিশ্ব"
লেখো("হ্যালো, " + নাম + "!")
`;

const jsCode = banglascript.compile(banglaCode);
console.log(jsCode);

require('banglascript/register');

// এখন সরাসরি .bs ফাইল require করা যাবে
const myModule = require('./myModule.bs');

চেষ্টা করো {
  // এমন কোড যা ত্রুটি দিতে পারে
  ধরি ফলাফল = বিপজ্জনক_ফাংশন()
  লেখো(ফলাফল)
} ধরো (ত্রুটি) {
  লেখো("একটি ত্রুটি ঘটেছে: " + ত্রুটি)
}
